import {
  addDoc,
  collection,
  doc,
  getDoc,
  getDocs,
  serverTimestamp,
  setDoc,
} from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { db } from "../firebase-config";

// import AccountDataService from "../services/account.services";

export const AccountList = () => {
  const [cities, setCities] = useState([]);

  useEffect(() => {
    getAccounts();
  }, []);

  const getAccounts = async () => {
    const list = [];

    try {
      const querySnap = await getDocs(collection(db, "accounts"));
      querySnap.forEach((res) => {
        list.push(res.data());
        // setCities(res.data());
        // console.log(res.data());
      });

      if (list.length != 0) {
        setCities(list);
      } else {
        console.log("No data found!");
      }
    } catch (error) {
      console.log(error);
    }

    // console.log(cities);

    // console.log(data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // const data = await addDoc(collection(db, "cities"), {
    //   name: "Los Angles",
    //   state: "LA",
    //   country: "USA",
    //   timeStamp: serverTimestamp(),
    // });

    const data = await addDoc(collection(db, "accounts"), {
      id: Date.now(),
      name: "scb bank 1",
      amount: 12000,
      timeStamp: serverTimestamp(),
    });

    console.log("Data Submited", data);
  };

  console.log(cities);

  return (
    <div>
      <h1>Account Lists</h1>
      <hr />
      <table className="table">
        <tr>
          <th>Total Amount</th>
          <th>
            {cities.map((accounts) => {
              const sum = accounts.reduce(
                (prev, curr) => prev + curr.amount,
                0
              );
              return sum;
            })}
          </th>
        </tr>
      </table>

      <br />
      <hr />

      <code>
        {cities &&
          cities.map((data, i) => {
            return (
              <>
                <h4 key={i}>{data.name + ", BDT " + data.amount}</h4> <br />
              </>
            );
          })}
      </code>

      <hr />
      <form onSubmit={handleSubmit}>
        <button className="btn btn-primary mt-5" type="submit">
          Save Data
        </button>
      </form>
    </div>
  );
};
