import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import React, { useState } from "react";
import { db } from "../firebase-config";

const AddTransaction = ({ accounts, categories }) => {
  const [values, setValues] = useState({
    type: "",
    amount: 0,
    note: "",
    bankOptions: [
      {
        name: "select",
        value: "",
      },
      ...accounts,
    ],
    category: "",
  });
  const [selected, setSelected] = useState(values.bankOptions[0].value);

  const handleInputChange = (e) => {
    const { name, value } = e.target;

    setValues({
      ...values,
      [name]: value,
    });

    console.log(values);
  };

  const handleSelectChange = (e) => {
    setValues({
      bank: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // const obj = {
    //   id: Date.now(),
    //   name: values.bankName,
    //   amount: parseInt(values.bankAmount),
    //   timeStamp: serverTimestamp(),
    // };

    console.log("Data: ", values);

    // if (values.bankName !== "" && values.bankAmount !== "") {
    //   // save data into firebase
    //   const data = await addDoc(collection(db, "accounts"), obj);

    //   console.log("Account Saved Successfully");
    // } else {
    //   console.log("Empty");
    // }
  };

  // console.log([
  //   {
  //     name: "select",
  //     value: "",
  //   },
  //   ...accounts,
  // ]);

  return (
    <div>
      <div className="row">
        <div className="col">
          <div className="card">
            <div className="card-header">Add Transaction</div>
            <div className="card-body">
              <form>
                {/* <div className="form-check form-check-inline">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="flexRadioDefault"
                    id="flexRadioDefault1"
                  />
                  <label
                    className="form-check-label"
                    htmlFor="flexRadioDefault1"
                  >
                    Expense
                  </label>
                </div>

                <div className="form-check form-check-inline">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="flexRadioDefault"
                    id="flexRadioDefault2"
                    checked
                  />
                  <label
                    className="form-check-label"
                    htmlFor="flexRadioDefault2"
                  >
                    Transfer
                  </label>
                </div> */}

                <hr />

                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    Amount
                  </label>
                  <input
                    type="number"
                    className="form-control"
                    name="amount"
                    onChange={handleInputChange}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    Note
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="note"
                    onChange={handleInputChange}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    Bank {"accounts"}
                  </label>

                  <select
                    className="form-select text-capitalize mb-3"
                    aria-label="Default select example"
                    name="bank"
                    onChange={handleSelectChange}
                    // defaultValue={"DEFAULT"}
                    value={values.bank}
                  >
                    {/* <option defaultValue={values.bank} disabled>
                      Select
                    </option> */}

                    {values.bankOptions &&
                      values.bankOptions.map((data, i) => {
                        return (
                          <option value={data.name} key={i}>
                            {data.name}
                          </option>
                        );
                      })}
                  </select>
                  {values.bank}
                </div>

                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    Category
                  </label>

                  <select
                    className="form-select text-capitalize mb-3"
                    aria-label="Default select example"
                    name="category"
                    onChange={handleInputChange}
                    // defaultValue={"DEFAULT"}
                    value={values.category}
                  >
                    {/* <option value={values.category} disabled>
                      Select
                    </option> */}

                    {categories &&
                      categories.map((data, i) => {
                        return (
                          <option value={data.name} key={i}>
                            {data.name}
                          </option>
                        );
                      })}
                  </select>
                </div>

                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddTransaction;
