import { db } from "../firebase-config";
import {
  collection,
  getDocs,
  getDoc,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";

const accountCollectionRef = collection(db, "accounts");

 const addAccounts = (newAccount) => {
  return addDoc(accountCollectionRef, newAccount);
};

 const updateAccount = (id, updateAccount) => {
  const accountDoc = doc(db, "accounts", id);
  return updateDoc(accountDoc, updateDoc);
};

 const deleteAccount = (id) => {
  const accountDoc = doc(db, "accounts", id);
  return deleteDoc(accountDoc);
};

 const getAllAccounts = () => {
  return getDocs(accountCollectionRef);
};

 const getAccount = (id) => {
  const accountDoc = doc(db, "accounts", id);
  return getDoc(accountDoc);
};

const AccountDataService = () => {
  return <div>AccountDataService</div>;
};

export default AccountDataService;
