/* eslint-disable no-unused-vars */
import {
  addDoc,
  collection,
  getDocs,
  serverTimestamp,
} from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { db } from "../firebase-config";

// import AccountDataService from "../services/account.services";

export const AccountList = () => {
  const [cities, setCities] = useState([]);

  useEffect(() => {
    getAccounts();
    // getTotalAmount();
  }, []);

  const getAccounts = async () => {
    const list = [];

    try {
      const querySnap = await getDocs(collection(db, "accounts"));
      querySnap.forEach((res) => {
        list.push(res.data());
      });

      if (list.length !== 0) {
        setCities(list);
      } else {
        console.log("No data found!");
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = await addDoc(collection(db, "accounts"), {
      id: Date.now(),
      name: "scb bank 1",
      amount: 12000,
      timeStamp: serverTimestamp(),
    });

    console.log("Data Submited", data);
  };

  const getTotalAmount = () => {
    const res = cities.reduce((a, v) => (a = a + v.amount), 0);

    return res;
  };

  // console.log(test);

  return (
    <div>
      <h1>Account Lists</h1>
      <hr />

      <h1>
        <span>Total Amount: BDT </span>
        <span className="text-primary">
          {cities
            ? cities.reduce(
                (total, currentValue) => (total += currentValue.amount),
                0
              )
            : "No Data Found!"}
        </span>
      </h1>

      {/* <table className="table">
        <tbody>
          <tr>
            <th>Total Amount</th>
            <th>{tss}</th>
          </tr>
        </tbody>
      </table> */}

      {/* {tss} */}

      <br />
      <hr />

      <code>
        {cities &&
          cities.map((data) => {
            return (
              <>
                <span>{data.name}</span>, <span>{data.amount} </span>
                <br />
              </>
            );
          })}
      </code>

      <hr />
      <form onSubmit={handleSubmit}>
        <button className="btn btn-primary mt-5" type="submit">
          Save Data
        </button>
      </form>
    </div>
  );
};
