import {
  addDoc,
  collection,
  doc,
  getDoc,
  getDocs,
  serverTimestamp,
  setDoc,
} from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { db } from "../firebase-config";

// import AccountDataService from "../services/account.services";

export const AccountList = () => {
  const [cities, setCities] = useState([]);

  useEffect(() => {
    getAccounts();
  }, []);

  const getAccounts = async () => {
    const list = [];

    try {
      const querySnap = await getDocs(collection(db, "cities"));
      querySnap.forEach((res) => {
        list.push(res.data());
        // setCities(res.data());
        // console.log(res.data());
      });

      setCities(list);
    } catch (error) {
      console.log(error);
    }

    console.log(cities);

    // console.log(data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = await addDoc(collection(db, "cities"), {
      name: "Los Angles",
      state: "LA",
      country: "USA",
      timeStamp: serverTimestamp(),
    });

    console.log("data submited", data);
  };

  return (
    <div>
      <h1>Account Lists</h1>
      <hr />
      <code>{/* <pre>{cities ? cities : ""}</pre> */}</code>
      <hr />
      <form onSubmit={handleSubmit}>
        <button className="btn btn-primary mt-5" type="submit">
          Save Data
        </button>
      </form>
    </div>
  );
};
