/* eslint-disable no-unused-vars */
import { async } from "@firebase/util";
import {
  addDoc,
  collection,
  getDocs,
  serverTimestamp,
} from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { db } from "../firebase-config";

// import AccountDataService from "../services/account.services";

export const AccountList = () => {
  const [cities, setCities] = useState([]);

  useEffect(() => {
    getAccounts();
  }, []);

  const getAccounts = async () => {
    const list = [];

    try {
      const querySnap = await getDocs(collection(db, "accounts"));
      querySnap.forEach((res) => {
        list.push(res.data());
      });

      if (list.length !== 0) {
        setCities(list);
      } else {
        console.log("No data found!");
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = await addDoc(collection(db, "accounts"), {
      id: Date.now(),
      name: "scb bank 1",
      amount: 12000,
      timeStamp: serverTimestamp(),
    });

    console.log("Data Submited", data);
  };

  const getTotalAmount = () => {
    const res = cities.reduce((a, v) => (a = a + v.amount), 0);

    console.log(res);
    // return res;
  };

  // console.log(test);

  const [category, setCategory] = useState([]);
  const [inputValue, setInputValue] = useState();

  const handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value;

    setInputValue({ [name]: value });
  };

  const addCategoryHandler = async (e) => {
    e.preventDefault();

    // setCategory({ name: inputValue });

    // const data = await addDoc(collection(db, "categories"), {
    //   id: Date.now(),
    //   name: inputValue,
    //   timeStamp: serverTimestamp(),
    // });

    console.log(inputValue);
  };

  return (
    <div>
      <span>Account Lists</span>
      <hr />

      <table className="table table-bordered">
        <thead>
          <tr className="bg-warning">
            <th>Total Amount</th>
            <th>
              {cities
                ? cities.reduce(
                    (total, currentValue) => (total += currentValue.amount),
                    0
                  )
                : "No Data Found!"}
            </th>
          </tr>
        </thead>
      </table>

      <table className="table table-bordered table-striped">
        <thead>
          <tr className="bg-secondary text-white">
            <th>Bank Name</th>
            <th>Amount</th>
          </tr>
        </thead>
        <tbody>
          {cities &&
            cities.map((data, i) => {
              return (
                <tr key={i}>
                  <td className="text-uppercase">{data.name}</td>
                  <td>{data.amount}</td>
                </tr>
              );
            })}
        </tbody>
      </table>

      <div className="row">
        <div className="col">
          <div className="card">
            <div className="card-header">Add Transaction</div>
            <div className="card-body">
              <form>
                <div className="form-check form-check-inline">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="flexRadioDefault"
                    id="flexRadioDefault1"
                  />
                  <label
                    className="form-check-label"
                    htmlFor="flexRadioDefault1"
                  >
                    Expense
                  </label>
                </div>

                <div className="form-check form-check-inline">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="flexRadioDefault"
                    id="flexRadioDefault2"
                    checked
                  />
                  <label
                    className="form-check-label"
                    htmlFor="flexRadioDefault2"
                  >
                    Transfer
                  </label>
                </div>

                <hr />

                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    Amount
                  </label>
                  <input type="number" className="form-control" id="number" />
                </div>
                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    Note
                  </label>
                  <input type="text" className="form-control" id="note" />
                </div>
                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    Category
                  </label>

                  <select
                    className="form-select mb-3"
                    aria-label="Default select example"
                  >
                    <option selected>Select</option>

                    {cities &&
                      cities.map((data, i) => {
                        return (
                          <option va={i} key={i}>
                            {data.name}
                          </option>
                        );
                      })}
                  </select>
                </div>

                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </form>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="card">
            <div className="card-header">Add Transaction</div>
            <div className="card-body">
              <form onSubmit={addCategoryHandler}>
                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    Category {category}
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="category"
                    name="category"
                    onChange={handleChange}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="" className="form-label">
                    hello {category}
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="hello"
                    name="hello"
                    onChange={handleChange}
                  />
                </div>

                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <button className="btn btn-primary mt-5" type="submit">
          Save Data
        </button>
      </form>
    </div>
  );
};
